#pragma config(Sensor, S1,     HTSPB,                sensorI2CCustom9V)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

/*
	Tiny program to randomly blink the blue and red LEDs on the SuperPro
*/

#include "drivers/hitechnic-superpro.h"

task main() {
  byte red;
  byte blue;

  while(true) {
    // Assign random value to the red and blue led to switch them on or off.
  	red = rand() % 2;
  	blue = rand() % 2;

    HTSPBsetLED(HTSPB, red, blue);

    wait1Msec(50);
  }
}
