#include "drivers/tris10-findball.h"

task main () {
  nNxtButtonTask  = -2;
  int strength_1;
  int strength_2;
  int strength_3;
  int strength_4;
  int strength_5;

  nxtDisplayCenteredTextLine(0, "Tris10");
  nxtDisplayCenteredBigTextLine(1, "FindBall");
  nxtDisplayCenteredTextLine(3, "Test 1");
  nxtDisplayCenteredTextLine(5, "Connect sensor");
  nxtDisplayCenteredTextLine(6, "to S1");
  wait1Msec(2000);

  eraseDisplay();
  nxtDisplayCenteredTextLine(0, "Tris10 FindBall");
  nxtDisplayTextLine(1, "-------------------");
  while (true) {

  	T10FBreadAllStrength(TR10FB, &strength_1, &strength_2, &strength_3, &strength_4, &strength_5);

    // Read the current data from the sensor
    nxtDisplayTextLine(2, "Dir: %3d: %3d ", T10FBreadDir(TR10FB), T10FBreadStrength(TR10FB));
    nxtDisplayTextLine(3, "  1: %3d", strength_1);
    nxtDisplayTextLine(4, "  2: %3d", strength_2);
    nxtDisplayTextLine(5, "  3: %3d", strength_3);
    nxtDisplayTextLine(6, "  4: %3d", strength_4);
    nxtDisplayTextLine(7, "  5: %3d", strength_5);

    for (int i = 0; i < 5; i++)
    {

    }
    wait1Msec(100);
  }
}
